mapper.select:
select
    *
from
    shop_info
where
    location_x < ${maxLocationX}
    and
    location_y < ${maxLocationY}
    and
    id != #{id}

mapper.update:
update shop_table_setting set
table_name = #{tableName}, allow_visitor = #{allowVisitor}, allow_start = #{allowStart}, allow_end = #{allowEnd}, time_div = #{timeDiv}, time_max = #{timeMax},
memo = #{memo}, enable = #{enable} where table_id = #{tableId}

mapper.select:
select * from shop_info i left join shophashtag s on i.id = s.member_id left join hashtag h on s.tag_id = h.tag_id
i.location_x < ${maxLocationX} and i.location_y < ${maxLocationY} and i.id != #{id} and h.tag_name in
#{tags}

mapper.update:
update shop_table_reservation
set reservation_status = '예약취소'
where reservation_no = #{reservationNo}
